import React from "react";

const AddOrder = () => {
  const [phone_number, setPhone_number] = useState("");
  const [sub_total, setSub_total] = useState("");

  const addItem = async () => {
    const data = await fetchData("POST", "/add-order", {
      phone_number,
      sub_total,
    });

    console.log(data);
    localStorage.setItem("accessToken", data.data.accessToken);

    if (data.statusCode === 200) {
      navigate("/");
    }
  };

  const addOrder = () => {
    console.log("added");
  };
  return (
    <div className=" flex w-full h-screen text-center justify-center bg-[#0F172A]">
      <div className="w-[50%] my-auto p-16 rounded-xl">
        <div>
          <h1 className="text-2xl text-white font-bold mb-5">Add Order</h1>
        </div>
        <form onSubmit={(e) => e.preventDefault()}>
          <input
            type="tel"
            className="w-full py-2 px-3 rounded-md mb-2"
            placeholder="Phone"
            onChange={(e) => setPhone_number(e.target.value)}
            value={phone_number}
          />
          <input
            type="number"
            className="w-full py-2 px-3 rounded-md mb-5"
            placeholder="Sub-Total"
            onChange={(e) => setSub_total(e.target.value)}
            value={sub_total}
          />
          <button
            className="bg-sky-700 w-full py-2 px-3 rounded-md text-white texl-2xl"
            onClick={addOrder}
            type="submit"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddOrder;
