import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { fetchData } from "../services/clients.js";

const Dashboard = () => {
  const [orders, setOrders] = useState();
  const [form, setForm] = useState("hidden");

  const toggleForm = () => {
    if (form === "hidden") {
      setForm("");
    } else {
      setForm("hidden");
    }
  };

  useEffect(
    (async () => {
      const data = await fetchData("GET", "/get-orders");
      setOrders(data.data);
      console.log(data);
    })(),
    []
  );

  return (
    <div className="text-white p-10">
      <div className="w-full py-5">
        <button
          className="text-white bg-sky-600 py-2 px-3 rounded-md"
          onClick={toggleForm}
        >
          Add Orded
        </button>
      </div>
      <div className={`w-[50%] my-auto p-16 rounded-xl ${form}`}>
        <div>
          <h1 className="text-2xl text-white font-bold mb-5">Login</h1>
        </div>
        <form onSubmit={(e) => e.preventDefault()}>
          <input
            type="text"
            className="w-full py-2 px-3 rounded-md mb-2"
            placeholder="Username"
          />
          <input
            type="password"
            className="w-full py-2 px-3 rounded-md mb-5"
            placeholder="password"
          />
          <button
            className="bg-sky-700 w-full py-2 px-3 rounded-md text-white texl-2xl"
            type="submit"
          >
            Login
          </button>
        </form>
      </div>

      <div className="grid grid-flow-col gap-5">
        {orders?.map((element) => {
          return (
            <>
              <Link to={`/order/${element._id}`}>
                <div className="w-max p-10 rounded-xl my-auto bg-slate-700 text-left">
                  <h1 className="text-5xl mb-5">Total</h1>
                  <h2 className="text-xl">
                    Order Prise : {element?.sub_total}
                  </h2>
                </div>
              </Link>
            </>
          );
        })}
      </div>
    </div>
  );
};

export default Dashboard;
